import React from "react";
import { useFormik } from "formik";

function App() {
  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    onSubmit: (values) => {
      if (values.email && values.password) {
        const emailRegex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        if (emailRegex.test(values.email)) {
          alert("Login Successful");
        } else {
          document.getElementById("emailError").innerHTML = "Username should be an email";
        }
      } else {
        if (!values.email) {
          document.getElementById("emailError").innerHTML = "Field required";
        }
        if (!values.password) {
          document.getElementById("pswError").innerHTML = "Field required";
        }
      }
    },
  });

  return (
    <form onSubmit={formik.handleSubmit}>
      <h1 className="text-3xl font-bold underline">
    Hello world!
    </h1>
      <label htmlFor="emailField" >Email:</label>
      <input
        id="emailField"
        name="email"
        type="email"
        onChange={formik.handleChange}
        value={formik.values.email}
      />
      <div id="emailError"></div>
      <br />
      <label htmlFor="pswField">Password:</label>
      <input
        id="pswField"
        name="password"
        type="password"
        onChange={formik.handleChange}
        value={formik.values.password}
      />
      <div id="pswError"></div>
      <br />
      <button id="submitBtn" type="submit">Submit</button>
    </form>
  );
}

export default App;
